<?php$jsondatkey = file_get_contents('balochxd72.json');$datkey = json_decode($jsondatkey, true);$data = json_decode(file_get_contents('balochxd72.json'), true);$license_key = $_GET["license_key"];$username = $_GET["user"];$expiration_date = $_GET["expiration_date"];$joined = $_GET["joined"];$key = $_GET['key'];if (isset($datkey[$key])) {  $expirationDate = strtotime($datkey[$key]['expirationDate']);  $currentDate = strtotime(date('Y-m-d'));  if ($expirationDate > $currentDate) {    http_response_code(200);    if (in_array($key, array_column($data, 'key'))) {        http_response_code(401);        exit(json_encode(array('status' => 'error', 'message' => 'The key is already used on another device')));    }else{        $data[$license_key] = array('name' => $username,'expirationDate' => $expiration_date,'joined' => $joined );        file_put_contents('balochxd72.json', json_encode($data));        echo json_encode(array('status' => 'success', 'message' => 'Your Key Approved Successfully'));    }  } else {    http_response_code(401);    echo json_encode(array('error' => 'Key has expired'));  }} else {  http_response_code(401);  echo json_encode(array('error' => 'you do not have the authority to perform this action'));}?>